  test.identical( provider.pathResolveSoftLink( dstPathTerminal ), srcPath2 );
  test.identical( provider.pathResolveSoftLink( srcPath2 ), srcPathTerminal );

  test.close( 'double' );

  /* */

  test.open( 'broken' );

  provider.filesDelete( routinePath );
