  let dstPath = test.context.pathFor( 'written/fileRenameRelativeTextLinking/dst' );

  /*
    src1 -> ../src2
    fileRename dst src1
    resolvingSrcTextLink : 0
    - link dst -> src1

    src1 -> ../src2
    fileRename dst src1
    resolvingSrcTextLink : 1
    - link dst -> ../src2

    src1 -> ../src2
    fileRename dst ../src1
    resolvingSrcTextLink : 0
    - link dst -> ../src1

    src1 -> ../src2
    fileRename dst ../src1
    resolvingSrcTextLink : 1
