  /*
    src1 -> ../src2
    softLink dst src1
    resolvingSrcSoftLink : 0
    - link dst -> src1

    src1 -> ../src2
    softLink dst src1
    resolvingSrcSoftLink : 1
    - link dst -> ../src2

    src1 -> ../src2
    softLink dst ../src1
    resolvingSrcSoftLink : 0
    - link dst -> ../src1

    src1 -> ../src2
    softLink dst ../src1
