    ({
      dstPath : linkPath1,
      srcPath : filePath1,
      rewriting : 1,
      rewritingDirs : 0
    });
  })
  test.true( provider.isDir( linkPath1 ) );
  test.true( provider.isTerminal( filePath1 ) );

  test.case = 'try to rewrite existing dir'
  provider.filesDelete( routinePath );
  provider.dirMake( linkPath1 );
  provider.fileWrite( filePath1, filePath1 );
  provider.textLink
  ({
    dstPath : linkPath1,
    srcPath : filePath1,
    rewriting : 1,
    rewritingDirs : 1
  });
  test.true( provider.isTextLink( linkPath1 ) );
  test.true( provider.isTerminal( filePath1 ) );
  var got = provider.pathResolveTextLink( linkPath1 );
