function filesSize( test )
{
  let context = this;
  let provider = context.provider;
  let path = provider.path;

  var textData1 = 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.'
  var textData2 = ' Aenean non feugiat mauris'
