    try
    {
      got1 = provider.filesSize( path );
    }
    catch( err )
    {
      _.errLog( err );
    }

    let expected = testCheck.expected;
    if( _.bigIntIs( got1 ) )
    expected = BigInt( expected );
    test.identical( got1, expected );
  }

