  test.identical( got, expected );

  var expected = current();
  var got = resolve( '.', 'aa', 'cc', '..', '..' );
  test.identical( got, expected );

  var expected = _.strIsolateRightOrNone( current(), '/' )[ 0 ];
  if( current() === '/' )
  expected = '/..';
  var got = resolve( 'aa', 'cc', '..', '..', '..' );
  test.identical( got, expected );

  /* */

  test.case = 'like-down or like-here cases';

