  provider.pathResolveLinkTailChain( o );
  var expectedFound = [ linkPath, filePath ]
  var expectedResult = [ linkPath, '../file' ]
  test.identical( o.result, expectedResult );
  test.identical( o.found, expectedFound );

  test.case = 'textLink';
