function pathNativize( t )
{
  let context = this;
  let provider = context.provider;

  if( !_.routineIs( provider.path.nativize ) )
  return;

  if( !( provider instanceof _.FileProvider.HardDrive ) )
  {
    t.description = 'nativize returns src'
    t.identical( 1, 1 )
    return;
  }

  if( Config.interpreter === 'njs' && process.platform === 'win32' )
  {
    t.description = 'path in win32 style ';

    /* */

    var path = '/A/abc/';
    var got = provider.path.nativize( path );
    var expected = 'A:\\abc\\';
    t.identical( got, expected );

    /* */

    var path = '/A/';
    var got = provider.path.nativize( path );
    var expected = 'A:\\';
    t.identical( got, expected );

    /* */

    var path = '/A';
    var got = provider.path.nativize( path );
    // var expected = 'A:\\';
    var expected = 'A:\\';
    t.identical( got, expected );

    /* */

    var path = '/A/a';
    var got = provider.path.nativize( path );
    var expected = 'A:\\a';
    t.identical( got, expected );

    /* */

    var path = 'A:/a';
    var got = provider.path.nativize( path );
    var expected = 'A:\\a';
    t.identical( got, expected );

    /* */

    var path = '\\A\\a';
    var got = provider.path.nativize( path );
    var expected = 'A:\\a';
    t.identical( got, expected );

    /* */

    var path = 'A';
    var got = provider.path.nativize( path );
    var expected = 'A';
    t.identical( got, expected );

    /* */

    var path = '/c/a';
    var got = provider.path.nativize( path );
    var expected = 'c:\\a';
    t.identical( got, expected );

    /* */

    var path = '/A/1.txt';
    var got = provider.path.nativize( path );
    var expected = 'A:\\1.txt';
    t.identical( got, expected );

    /* */

    var path = 'A:/a\\b/c\\d';
