function fileCopyExperiment( test )
{
  let context = this;
  let provider = context.provider;
  let path = provider.path;

  let routinePath = test.context.pathFor( 'written/fileCopyExperiment' );
  let srcPath = provider.path.join( routinePath, 'src' );
